function bfsTraversal(graph, start) {
  const visited = new Set();
  const result = [];
  const queue = [start];

  while (queue.length) {
    const node = queue.shift();
    if (visited.has(node)) continue;
    visited.add(node);
    result.push(node);

    const neighbors = graph[node] || [];
    for (const next of neighbors) {
      if (!visited.has(next)) queue.push(next);
    }
  }

  return result;
}
