function dfsTraversal(graph, start) {
  const visited = new Set();
  const result = [];

  function dfs(node) {
    if (visited.has(node)) return;
    visited.add(node);
    result.push(node);
    const neighbors = graph[node] || [];
    for (const next of neighbors) {
      dfs(next);
    }
  }

  dfs(start);
  return result;
}
