function minCostClimbingStairs(cost) {
  const n = cost.length;
  if (n === 0) return 0;
  if (n === 1) return 0;

  let prev2 = cost[0];
  let prev1 = cost[1];
  for (let i = 2; i < n; i += 1) {
    const cur = cost[i] + Math.min(prev1, prev2);
    prev2 = prev1;
    prev1 = cur;
  }

  return Math.min(prev1, prev2);
}
