walkthrough.body:
function isValid(s) {
  const pairs = {
    "(": ")",
    "[": "]",
    "{": "}",
  };

  const stack = [];
  for (const ch of s) {
    if (pairs[ch]) {
      stack.push(ch);
      continue;
    }

    const left = stack.pop();
    if (!left || pairs[left] !== ch) return false;
  }

  return stack.length === 0;
}
